<style module lang="scss">
.googleAuthBtn {
	display: inline-flex;
	cursor: pointer;
	border: none;
	padding: 0;
	background: transparent;
	border-radius: var(--radius);

	&:hover,
	&:active {
		svg {
			filter: brightness(0.9);
		}
	}

	&:active svg {
		filter: brightness(0.9);
	}

	&:focus {
		outline: none;
	}

	&:focus-visible {
		outline: var(--focus--border-width, 1px) solid
			var(--focus--border-color, var(--color--secondary));
		outline-offset: 2px;
	}

	&:disabled {
		svg {
			filter: brightness(0.6);
		}
		cursor: not-allowed;
	}
}
</style>
